arguments.argument:
x = owner.x;
y = owner.y;

draw_sprite_ext(sprite_index,image_index,owner.x,owner.y,image_xscale,image_yscale,0,c_white,alpha);

var offset;
if !place_meeting(owner.x,owner.y+1,Obstacle)
    and!place_meeting(owner.x,owner.y+1,PlayerWallHorizontal)
    offset = 2;
else
    offset = 0;

draw_sprite_ext(BackstabLegsS, owner.team+offset,owner.x, owner.y,image_xscale,image_yscale,0,c_white,alpha);

if (owner.ubered)
{
    if team == TEAM_RED ubercolour = c_red;
    if team == TEAM_BLUE ubercolour = c_blue;
    draw_sprite_ext(sprite_index,image_index,owner.x,owner.y,image_xscale,image_yscale,image_angle,ubercolour,min(alpha, 0.7));
    draw_sprite_ext(BackstabLegsS, owner.team+offset,owner.x,owner.y,image_xscale,image_yscale,0,ubercolour,min(alpha, 0.7));
}
if(owner.kritzed){
    if team == TEAM_RED ubercolour = c_orange;
    if team == TEAM_BLUE ubercolour = c_aqua;
    draw_sprite_ext(sprite_index,image_index,owner.x,owner.y,image_xscale*1.5,image_yscale*1.5,image_angle,ubercolour,min(alpha, 0.7));
}
